INSERT INTO words (entry, pronunciation, pos, abbreviation, sa, field_of_study, notes, meaning) VALUES ('a priori', 'ˌɑː priːˈɔːr.aɪ /ˌeɪ priːˈɔːr.aɪ', 'adjective, adverb', '', '', 'معناشناسی، فلسفه', 'گزاره‌ای که صدق آن مستقل از تجربه و صرفاً بر پایۀ عقل یا استدلال منطقی تعیین می‌شود', 'پیشینی (دانش)');
