INSERT INTO words (entry, pronunciation, pos, abbreviation, sa, field_of_study, notes, meaning) VALUES ('absolute', 'ˈæb.sə.luːt ', 'adjective', '', '', 'نحو، معناشناسی', 'به ساختاری گفته می‌شود که از نظر نحوی مستقل از بند اصلی جمله است و معمولاً اطلاعات اضافی درباره زمان، علت، یا شرایط ارائه می‌دهد.', 'مطلق');
